<?php
namespace Mshiba\Px2lib\Asazuke;
class AsazukeUtilConsole
{

    private $n = 0;

    public function __construct()
    {
        date_default_timezone_set("Asia/Tokyo");
        echo "\n";
        echo "開始時間：" . date('H:i:s') . "\n";
        echo str_repeat("*", 60) . "\n";
    }

    public function out($buffer)
    {
        $aryBuffer = AsazukeUtil::str2array($buffer);
        $n = count($aryBuffer) - 1;

        $this->n = max($this->n, $n);

        echo "\033[K"; // カーソル位置から行末までをクリア
        echo implode("\n", $aryBuffer);
        echo "\033[" . $n . "A" . "\r"; // n行前に戻る、キャッリッジ・リターン
    }

    public function close()
    {
        echo "\033[" . $this->n . "B" . "\n"; // n行前に戻る、改行

        echo str_repeat("*", 60) . "\n";

        echo "終了時間：" . date('H:i:s') . "\n";
        echo "\n";
        echo "\n";
    }
}

